mapper.update:
update address
set userid = #{userid,jdbcType=INTEGER},
  name = #{name,jdbcType=VARCHAR},
  phone = #{phone,jdbcType=VARCHAR},
  address = #{address,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, userid, name, phone, address
from address
where id = #{id,jdbcType=INTEGER}
