mapper.update:
update address
set userid = #{userid,jdbcType=VARCHAR},
  name = #{name,jdbcType=VARCHAR},
  phone = #{phone,jdbcType=VARCHAR},
  address = #{address,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, userid, name, phone, address
from address
where id = #{id,jdbcType=VARCHAR}
