mapper.update:
update myuser
set name = #{name,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR},
  icon = #{icon,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, name, password, icon
from myuser
where id = #{id,jdbcType=VARCHAR}

select id, name, password, icon
from myuser
where name = #{name}
